import React, { useState, useRef } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './index.css';

function Review(props) {
  const [reviewText, setReviewText] = useState('');
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const db = getDatabase();
    const reviewsRef = ref(db, 'reviews');
    const reviewsListener = onValue(reviewsRef, (snapshot) => {
      const reviewsData = snapshot.val();
      if (reviewsData) {
        const reviewsArray = Object.keys(reviewsData).map(key => ({
          id: key,
          ...reviewsData[key]
        }));
        setReviews(reviewsArray);
      }
    });

    return () => {
      off(reviewsRef, 'value', reviewsListener);
    };
  }, []);


  const handleChange = (event) => {
    setReviewText(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const db = getDatabase();
    const reviewsRef = ref(db, 'reviews');
    const newReviewRef = push(reviewsRef);
    set(newReviewRef, {
      text: reviewText,
    }).then(() => {
      setReviewText('');
    }).catch(error => {
      console.error('Error adding review: ', error);
    });
  };

  return (
    <div className="review-form">
      <form onSubmit={handleSubmit}>
        <textarea className="review-text" placeholder="Write your review here..." value={reviewText} onChange={handleChange} />
        <button type="submit" className="submit-button">Submit Review</button>
      </form>
    </div>
  );
}

export { Review };
