import React, { useState, useRef, useEffect } from 'react';
import { db } from './firebase';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './index.css';

function Review(props) {
  const [reviewText, setReviewText] = useState('');
  const [reviews, setReviews] = useState([]);

  const handleChange = (event) => {
    setReviewText(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!reviewText) return;
    try {
      const newReviewRef = await db.ref('reviews').push({
        content: reviewText,
      });
      setReviewText('');
    } catch (error) {
      console.error('Error', error);
    }
  };

  useEffect(() => {
    const reviewRef = db.ref('reviews');
    const unsubscribe = reviewRef.on('value', (snapshot) => {
      const data = snapshot.val() || {};
      setReviews(Object.values(data));
    });

    return unsubscribe;
  }, []);

  return (
    <div className="review-form">
      <form onSubmit={handleSubmit}>
        <textarea className="review-text" placeholder="Write your review here..." value={reviewText} onChange={handleChange} />
        <button type="submit" className="submit-button">Submit Review</button>
      </form>
      {reviews.length === 0 ? (
        <p>Loading reviews...</p>
      ) : (
        <ul className="reviews-list">
          {reviews.map((review) => (
            <li key={review.id}>
              <p>{review.content}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export { Review };
