import React, { useState } from 'react';

function JobListings() {
  const [searchTerm, setSearchTerm] = useState('');

  function handleSearchInputChange(event) {
    setSearchTerm(event.target.value.toLowerCase());
  }

  function searchJobs() {
    const jobListings = document.querySelectorAll('.job-listing');

    jobListings.forEach((listing) => {
      const title = listing.dataset.title.toLowerCase();

      if (title.includes(searchTerm)) {
        listing.style.display = 'block';
      } else {
        listing.style.display = 'none';
      }
    });
  }

  function suggestSearch(keyword) {
    document.getElementById('searchInput').value = keyword;
    searchJobs();
  }

  return (
    <div className="right-section">
      <div className="searchBar">
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Search for a job..."
            id="searchInput"
            value={searchTerm}
            onChange={handleSearchInputChange}
          />
          <button className="btn btn-primary" type="button" onClick={searchJobs}>
            Search
          </button>
        </div>
      </div>

      <div className="btn-group">
        <button
          type="button"
          className="btn btn-secondary"
          onClick={() => suggestSearch('Barista')}
        >
          Barista
        </button>
        <button
          type="button"
          className="btn btn-secondary"
          onClick={() => suggestSearch('Electrician')}
        >
          Electrician
        </button>
        <button
          type="button"
          className="btn btn-secondary"
          onClick={() => suggestSearch('Janitor')}
        >
          Janitor
        </button>
      </div>

      {/* Rest of your job listings */}
    </div>
  );
}

export default JobListings;
